$("a").on('click', function (e) {
  if (this.hash && this !== '') {
    let hashTag = this.hash;
    $('html,body').animate({
      scrollTop: $(hashTag).offset().top
    },700)
  }
})

const preloader = document.querySelector('.page-preloder')
window.addEventListener('load',(e)=>{
preloader.classList.add('end-preloder')
})
const head = document.querySelector(".head-navigation");
window.addEventListener("scroll", () => {
  head.classList.toggle("sticky", window.scrollY > 0);
});





const toggleBtn = document.querySelector(".toggle-bar");
const closeBtn = document.querySelector(".close-btn");
const sidebar = document.querySelector(".slide-bar");

toggleBtn.addEventListener("click", function () {
  sidebar.classList.toggle("active-mobile-slider");
  const link = document.querySelectorAll(".slide-links li a");

  for (let i = 0; i < link.length; i++) {
    const element = link[i];
    element.addEventListener("click", (e) => {
      setTimeout(() => { 
        sidebar.classList.remove("active-mobile-slider");
      }, 200);
    });
  }
});
closeBtn.addEventListener("click", function () {
  sidebar.classList.remove("active-mobile-slider");
});


AOS.init({
    duration: 1000,
    once: true,
});


$('.state-count').counterUp({
    delay: 10,
    time: 1000,
    offset: 70,
    formatter: function (n) {
        return n.replace(/,/g, '.');
    }
})

const openAns = document.querySelectorAll('.btn-plus')
const ansContainer = document.querySelectorAll('.quistion-single')


openAns.forEach((elem) => {
    elem.addEventListener('click', (val) => {
        ansContainer.forEach(e => {
            if(e.classList.contains('show-quiestion')){
            e.classList.remove('show-quiestion')
            }
        })
        const container = val.currentTarget.parentElement.parentElement.parentElement
        container.classList.add('show-quiestion')
    })

})

const closeAns = document.querySelectorAll('.minus')
closeAns.forEach((e)=>{
    e.addEventListener('click',(elem)=>{
        const r = elem.currentTarget.parentElement.parentElement.parentElement
        r.classList.remove('show-quiestion')
        })
})
